solution_code.python:
a = float(input()); b = float(input())
if a == 0:
    print('Vo so nghiem' if b == 0 else 'Vo nghiem')
else:
    print(f'{-b/a:.2f}')

solution_code.c:
#include <stdio.h>

int main() {
    float a, b; scanf("%f %f", &a, &b);
    if(a==0) { if(b==0) printf("Vo so nghiem"); else printf("Vo nghiem"); }
    else printf("%.2f", -b/a);
    return 0;
}

solution_code.cpp:
#include <iostream>
using namespace std;

int main() {
    float a, b; cin >> a >> b;
    if(a==0) { if(b==0) cout << "Vo so nghiem"; else cout << "Vo nghiem"; }
    else printf("%.2f", -b/a);
    return 0;
}

solution_code.java:
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        double a = sc.nextDouble(), b = sc.nextDouble();
        if(a==0) System.out.println(b==0 ? "Vo so nghiem" : "Vo nghiem");
        else System.out.printf("%.2f", -b/a);
    }
}

solution_code.csharp:
using System;
using System.Linq;
using System.Collections.Generic;

class Program {
    static void Main() {
        double a = double.Parse(Console.ReadLine()); double b = double.Parse(Console.ReadLine());
        if(a==0) Console.WriteLine(b==0 ? "Vo so nghiem" : "Vo nghiem");
        else Console.WriteLine((-b/a).ToString("F2"));
    }
}

