solution_code.c:
#include <stdio.h>

int main() {
    int km; scanf("%d", &km); long long total=0;
    if(km>=1) total += 10000;
    if(km>1) { int d = (km>30?29:km-1); total += d*12000; }
    if(km>30) total += (long long)(km-30)*11000;
    printf("%lld", total);
    return 0;
}

solution_code.cpp:
#include <iostream>
using namespace std;

int main() {
    int km; cin >> km; long long total=0;
    if(km>=1) total += 10000;
    if(km>1) { int d = (km>30?29:km-1); total += d*12000; }
    if(km>30) total += (long long)(km-30)*11000;
    cout << total;
    return 0;
}

solution_code.java:
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int km = sc.nextInt(); long total = 0;
        if(km >= 1) total += 10000;
        if(km > 1) total += (long)Math.min(km-1, 29) * 12000;
        if(km > 30) total += (long)(km-30) * 11000;
        System.out.println(total);
    }
}

solution_code.csharp:
using System;
using System.Linq;
using System.Collections.Generic;

class Program {
    static void Main() {
        int km = int.Parse(Console.ReadLine()); long total = 0;
        if(km >= 1) total += 10000;
        if(km > 1) total += (long)Math.Min(km-1, 29) * 12000;
        if(km > 30) total += (long)(km-30) * 11000;
        Console.WriteLine(total);
    }
}

