#include <stdio.h>

int main() {
    int n; if(scanf("%d", &n)!=1) return 0; char tmp[100]; char names[100][100];
    getchar();
    for(int i=0; i<n; i++) { fgets(names[i], 100, stdin); int l = strlen(names[i]); if(l && names[i][l-1]=='\n') names[i][l-1] = 0; }
    for(int i=0; i<n-1; i++) for(int j=i+1; j<n; j++) if(strcmp(names[i], names[j])>0) { strcpy(tmp, names[i]); strcpy(names[i], names[j]); strcpy(names[j], tmp); }
    for(int i=0; i<n; i++) printf("%s
", names[i]);
    return 0;
}

