import sys
def is_prime(n):
    if n < 2: return False
    for i in range(2, int(n**0.5) + 1): 
        if n % i == 0: return False
    return True
line = sys.stdin.read().split()
if len(line) >= 2:
    for i in range(int(line[0]), int(line[1]) + 1):
        if is_prime(i): print(i)

starter_code.c:
#include <stdio.h>
int isPrime(int n) {
    if (n < 2) return 0;
    for (int i = 2; i * i <= n; i++) if (n % i == 0) return 0;
    return 1;
}
int main() {
    int A, B;
    if (scanf("%d %d", &A, &B) == 2) {
        for (int i = A; i <= B; i++) if (isPrime(i)) printf("%d\n", i);
    }
    return 0;
}

starter_code.cpp:
#include <iostream>
using namespace std;
bool isPrime(int n) {
    if (n < 2) return false;
    for (int i = 2; i * i <= n; i++) if (n % i == 0) return false;
    return true;
}
int main() {
    int A, B;
    if (cin >> A >> B) {
        for (int i = A; i <= B; i++) if (isPrime(i)) cout << i << endl;
    }
    return 0;
}

