import sys
def factorial(n):
    return 1 if n == 0 else n * factorial(n-1)
line = sys.stdin.read().strip()
if line: print(factorial(int(line)))

starter_code.c:
#include <stdio.h>
long long fact(int n) {
    return (n == 0) ? 1 : n * fact(n-1);
}
int main() {
    int n;
    if (scanf("%d", &n) == 1) printf("%lld", fact(n));
    return 0;
}

